def fast_pow(x, e, m):
    return tomsfastmath.fast_pow(x, e, m)


def pow3(x, y, z):
    ans = 1
    while y:
        if y & 1:
            ans = (ans * x) % z
        y >>= 1
        if not y:
            break
        x = (x * x) % z
    return ans


